.search-bar {
  margin-bottom: 2rem;
  width: 100%;
  padding-right: 2rem;
  position: relative;
  height: 2.5rem;

  &__input {
    width: 100%;
    padding: 1rem 1.4rem;
    font-size: 1.5rem;
    border-radius: 20rem;
    outline: none;
    border: none;
    box-shadow: 0 1px 5px rgba(0, 0, 0, 0.2);
    font-family: "Abel", sans-serif;
    position: absolute;
    left: 0;

    &::placeholder {
      color: #a6a6a6;
    }
  }

  &__submit {
    font-size: 1.6rem;
    background: color(primary);
    color: white;
    border-radius: 5rem;
    width: 4rem;
    height: 4rem;
    text-align: center;
    vertical-align: middle;
    cursor: pointer;
    display: inline-block;
    position: absolute;
    right: 0;
    border: none;
    z-index: 2;
  }
}

.content-list {
  flex: 0 30rem;
  display: flex;
  flex-direction: column;
  position: relative;

  &__button {
    height: 4rem;
    display: flex;
    margin-right: 2rem;
    position: relative;
    width: 26em;
    border-radius: 10rem;
    cursor: pointer;
    border: none;
    background-color: color(primary);
    margin-bottom: 1.6rem;
    outline: none;
    transition: 0.3s;
    margin-left: 1rem;
    box-shadow: 0 3px 5px rgba(0, 0, 0, 0.2);
    font-family: $font-primary;

    &:hover {
      opacity: 0.9;
    }
  }

  &__image {
    height: 5rem;
    background-color: #fdfeff;
    border-radius: 100%;
    flex: 0 0 5rem;
    transform: translate(-0.5rem, -0.5rem);
    box-shadow: 0 1px 5px rgba(0, 0, 0, 0.2);
  }

  &__desc {
    font-size: 1.6rem;
    font-weight: 300;
    text-transform: uppercase;
    padding: 0.9rem;
    color: color(white-1);
  }

  @media all and (max-width: 800px) {
    & {
      flex: 1;
      justify-content: center;
    }

    &__button {
      width: 95%;
    }
  }
}

.show-box-container {
  height: 100%;
  flex: 1;

  @media all and (max-width: 800px) {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.3);
    z-index: 999;
    display: none;
  }

  &.is-visible {
    @media all and (max-width: 800px) {
      display: block;
    }
  }
}

.show-box {
  background-color: #f5f7fa;
  // height: 100%;
  flex: 1;
  border-top-right-radius: 3px;
  border-top-left-radius: 3px;
  border-bottom-right-radius: 3px;
  padding: 0 2rem;

  &__close {
    display: none;
    position: absolute;
    top: 1rem;
    right: 1rem;
    height: 3rem;
    width: 3rem;
    text-align: center;
    font-size: 2rem;
    padding-top: 0.2rem;
    background-color: color(primary);
    color: color(white-1);
    border-radius: 4px;
    cursor: pointer;

    @media all and (max-width: 800px) {
      display: inline-block;
    }
  }

  &__image {
    display: block;
    margin: 5rem auto;
    width: 20rem;
    height: 20rem;
    background-color: white;
    border-radius: 100%;
    display: flex;
    justify-content: center;
    align-items: center;

    & img {
      height: 150px;
    }
  }

  &__text {
    text-align: center;
    padding: 0 2rem;
  }

  & h3 {
    text-transform: uppercase;
  }

  @media all and (max-width: 800px) {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 90%;
    transform: translate(-50%, -50%);
    background-color: color(white-1);
    height: 95%;
    max-height: 800px;
    z-index: 1000;
    box-shadow: 0 3px 5px rgba(0, 0, 0, 0.3);
    overflow: scroll;
    padding-bottom: 4rem;
  }
}

/* Type Classes */
.content-list__type {
  border-radius: 3px;
  padding: 2px 4px;
  opacity: 0.8;
  display: inline;
  margin-right: 0.5rem;

  &--fire {
    background-color: #f08030;
  }
  &--grass {
    background-color: #22c02a;
  }
  &--electric {
    background-color: #f3ef08;
  }
  &--ground {
    background-color: #6e6d36;
  }
  &--fairy {
    background-color: #e441ce;
  }
  &--flying {
    background-color: #aad7fc;
  }
  &--bug {
    background-color: #095e10;
  }
  &--psychic {
    background-color: #3c095e;
  }
  &--rock {
    background-color: #633d0c;
  }
  &--steel {
    background-color: #909596;
  }
  &--ice {
    background-color: #0dd7ff;
  }
  &--ghost {
    background-color: #ddacf3;
  }
  &--fighting {
    background-color: #5e1609;
  }
  &--water {
    background-color: #6890f0;
  }
  &--poison {
    background-color: #9f409f;
  }
  &--normal {
    background-color: #a6a6a6;
  }
}

.lds-dual-ring {
  display: inline-block;
  width: 64px;
  height: 64px;
  position: absolute;
  top: 10rem;
  left: 40%;
  transform: translate(-50% -50%);
  &:after {
    content: " ";
    display: block;
    width: 46px;
    height: 46px;
    margin: 1px;
    border-radius: 50%;
    border: 5px solid color(primary);
    border-color: color(primary) transparent color(primary) transparent;
    animation: lds-dual-ring 1.2s linear infinite;
  }
}

@keyframes lds-dual-ring {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

.header {
  width: 100%;
  height: 6rem;
  // background-color: orangered;
  display: flex;
  padding: 0 2rem;
}

.logo-box {
  flex: 1;
  height: 100%;
  display: flex;
  justify-content: flex-start;
  align-items: flex-end;
}

.logo {
  font-size: 3rem;
  color: color(white);
}

.search-box {
  height: 100%;
  display: flex;
  width: 7.7rem;
  justify-content: flex-end;
  align-items: flex-end;
  position: relative;
  transition: all 1s ease;
  &::after {
    content: '';
    position: absolute;
    bottom: 0;
    right: 0;
    height: 3px;
    width: 100%;
    background-color: color(primary);
  }
  &:hover {
    width: 60%;
    &::after {
      box-shadow: 0 0 5px color(primary);
    }
  }
}

.search {
  font-size: 1.6rem;
  text-align: right;
  padding: 0.5rem;
  border: none;
  // border-bottom: 2px solid color(primary);
  width: 8rem;
  background-color: transparent;
  color: color(primary);
  transition: all 0.3s ease;
  &::placeholder {
    color: color(primary);
  }
  &:focus {
    outline: none;
  }
}

.items-container {
  height: 100%;
  overflow: scroll;
  padding: 2rem;
  margin-top: 5rem;
  padding-bottom: 20rem;
  position: relative;
  &::after {
    content: '';
    position: fixed;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 10%;
    background: linear-gradient(to top, rgba(23, 26, 30, 1), rgba(23, 26, 30, 0.95), rgba(42, 47, 54, 0));
  }
}


::-webkit-scrollbar {
  width: 4px;
}
::-webkit-scrollbar-track {
  background: color(dark-gray);
}

::-webkit-scrollbar-thumb {
  background: color(primary);
}

.item {
  height: 5rem;
  width: 100%;
  text-align: right;
  padding: 0.5rem;
  font-size: 1.6rem;
  color: color(white);
  background-color: transparent;
  border: none;
  border-bottom: 1px solid color(medium-gray);
  transition: all 0.6s ease;
  cursor: pointer;
  &:first-child {
    border-top: 1px solid color(medium-gray);
  }
  &:hover {
    background: linear-gradient(to right, rgba(67, 68, 69, 0), rgba(34, 36, 40, 1));
  }
}

.details-nav {
  position: absolute;
  top: 1%;
  right: 1%;
  height: 5rem;
  width: 100%;
  text-align: right;
  z-index: 2;
}

.back {
  height: 5rem;
  width: 5rem;
  border: 2px solid color(primary);
  font-size: 3rem;
  color: color(primary);
  background-color: transparent;
}

.image-box {
  flex: 1;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
}

.details-image {
  height: 50%;
}

.details-body {
  flex: 1;
  width: 100%;
  // background-color: violet;
}

.details-name {
  display: inline-block;
  height: 3rem;
  min-width: 20rem;
  font-size: 5rem;
  color: color(white);
  margin-bottom: 4rem;
}

.placeholder {
  background-color: color(light-gray);
}

.other-detail {
  border-bottom: 1px solid color(light-gray);
  padding-bottom: 1.2rem;
  margin-bottom: 1.5rem;
}

.other-detail__label {
  color: color(light-gray);
  font-size: 1.8rem;
  padding-bottom: 1rem;
}

.other-detail__value {
  color: color(white);
  font-size: 2.5rem;
  display: inline-block;
  margin-right: 1rem;
  height: 2rem;
  min-width: 4rem;
}

@media screen and (min-width: 600px) {
  .items-container::after {
    width: 50%;
  }
}

